import React, { Component } from "react";
import * as RB from "react-bootstrap";

export default class DoctorOrderItem extends Component {
  state = {
    edit: false
  };

  handleOrders = () => {
    console.log("hey");
    this.setState(state => ({
      edit: !state.edit
    }));
  };

  render() {
    console.log(this.props);

    return (
      <div>
        {this.props.drug.name}
        <p>Purchased by: {this.props.user.first_name} </p>
        {this.props.confirmed === false ? (
          <RB.Button variant="danger">UnConfirmed</RB.Button>
        ) : (
          <RB.Button variant="primary">Confirmed</RB.Button>
        )}
      </div>
    );
  }
}
