import React, { Component } from "react";
import { withRouter } from "react-router-dom";
import * as ReactBootstrap from "react-bootstrap";

const Orders_URL = "http://localhost:3001/orders";

class DrugDetail extends Component {
  state = {
    orders: this.props.orders
  };

  makeOrder = () => {
    console.log("click");
    const newOrder = {
      user: this.props.user,
      drug: this.props.drug,
      order_type: "",
      cost: 0
    };
  };

  render() {
    console.log(this.props);
    if (!this.props.drug) {
      return <div></div>;
    }

    return (
      <div>
        <p>{this.props.drug.name}</p>
        <ReactBootstrap.Dropdown>
          <ReactBootstrap.Dropdown.Toggle variant="success" id="dropdown-basic">
            Dropdown Button
          </ReactBootstrap.Dropdown.Toggle>

          <ReactBootstrap.Dropdown.Menu>
            <ReactBootstrap.Dropdown.Item href="#/action-1">
              Action
            </ReactBootstrap.Dropdown.Item>
            <ReactBootstrap.Dropdown.Item href="#/action-2">
              Another action
            </ReactBootstrap.Dropdown.Item>
            <ReactBootstrap.Dropdown.Item href="#/action-3">
              Something else
            </ReactBootstrap.Dropdown.Item>
          </ReactBootstrap.Dropdown.Menu>
        </ReactBootstrap.Dropdown>
      </div>
    );
  }
}

export default withRouter(DrugDetail);
