import React from "react";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Redirect
} from "react-router-dom";

import Navbar from "./NavBar";
import Login from "./login";

import "./App.css";

const USER_PROFILE_URL = "http://localhost:3000/api/v1/profile ";

export default class App extends React.Component {
  state = {
    currentUser: null
  };

  componentDidMount() {
    let token = localStorage.getItem("jwt");
    console.log(token);

    if (token) {
      Promise.all([
        fetch(opp_url),
        fetch(org_url),
        fetch(Log_URL),
        fetch(USER_PROFILE_URL, {
          headers: {
            Authorization: `Bearer ${token}`
          }
        })
          .then(res => res.json())
          .then(data => {
            // debugger
            this.onChangeUser(data.user_data);
          })
      ]);
    }
  }

  render() {
    return (
      <Router>
        <div className="App">
          <Navbar />
          <Switch>
            <Route path="/login" component={Login} />
          </Switch>
        </div>
      </Router>
    );
  }
}
