import React, { Component } from "react";
import * as ReactBootstrap from "react-bootstrap";
// import ToggleButton from "react-bootstrap/ToggleButton";

export default class Survey extends Component {
  render() {
    const user = this.props.user;
    console.log(user.answer1);
    return (
      <div>
        <>
          {" "}
          Hello
          <ReactBootstrap.ToggleButtonGroup
            name="question1"
            type="radio"
            // defaultValue={[user.answer1]}
            className="mb-2"
            variant="dark"
            size="sm"
          >
            <ReactBootstrap.ToggleButton variant="secondary" value={1}>
              Checkbox 1
            </ReactBootstrap.ToggleButton>
            <ReactBootstrap.ToggleButton value={2}>
              Checkbox 2
            </ReactBootstrap.ToggleButton>
            <ReactBootstrap.ToggleButton value={3}>
              Checkbox 3
            </ReactBootstrap.ToggleButton>
          </ReactBootstrap.ToggleButtonGroup>
          <br />
        </>
      </div>
    );
  }
}

