import React from "react";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  withRouter,
  Redirect
} from "react-router-dom";

import Navbar from "./NavBar";
import Login from "./Login";
import Profile from "./Profile";

import "./App.css";

const USER_PROFILE_URL = "http://localhost:3001/profile ";

class App extends React.Component {
  state = {
    currentUser: null
  };

  componentDidMount() {
    let token = localStorage.getItem("jwt");
    console.log(token);

    if (token) {
      // debugger;
      Promise.all([
        // fetch(opp_url),
        // fetch(org_url),
        // fetch(Log_URL),
        fetch(USER_PROFILE_URL, {
          headers: {
            Authorization: `Bearer ${token}`
          }
        })
          .then(res => res.json())
          .then(data => {
            // debugger
            this.onChangeUser(data.user_data);
          })
      ]);
    }
  }

  onChangeUser = user => {
    this.setState({ currentUser: user, userlogin: true });
    // debugger
  };

  render() {
    return (
      <Router>
        <div className="App">
          <Navbar
            user={this.state.currentUser}
            onChangeUser={this.onChangeUser}
          />
          <Switch>
            <Route exact path="/" render={() => <Redirect to="/login" />} />
            <Route
              exact
              path="/login"
              render={() => {
                return this.state.currentUser === null ? (
                  <Login onChangeUser={this.onChangeUser} />
                ) : null;
              }}
            />
            <Route
              exact
              path="/profile"
              render={() => {
                return this.state.currentUser === null ? (
                  <Redirect to="/login" />
                ) : (
                  <Profile user={this.state.currentUser} />
                );
              }}
            />
          </Switch>
        </div>
      </Router>
    );
  }
}

export default withRouter(App);
